custom_css:
/* ✴ PG Celestial - Starbound ✴ */
/* Star and chain accents */
.mes[is_user="false"] .mes_text::before {
  content: '✴';
  position: absolute;
  top: -6px;
  left: -6px;
  font-size: 10px;
  color: rgba(227, 179, 95, 0.35);
  pointer-events: none;
}
.mes[is_user="false"] .mes_text::after {
  content: '⚬';
  position: absolute;
  bottom: -4px;
  right: -4px;
  font-size: 8px;
  color: rgba(251, 224, 156, 0.25);
  pointer-events: none;
}
.mes .mes_text { position: relative; }
/* Golden chain-link border */
.mes[is_user="false"] {
  border-left: 2px solid rgba(227, 179, 95, 0.25) !important;
  box-shadow: inset 2px 0 12px rgba(227, 179, 95, 0.04);
}
/* Starbound separator with shimmer */
.mes + .mes::before {
  content: '⚬ ✴ ⚬';
  display: block;
  text-align: center;
  font-size: 7px;
  color: rgba(227, 179, 95, 0.15);
  padding: 2px 0;
  letter-spacing: 4px;
  animation: celestialShimmer 4s ease-in-out infinite;
}
@keyframes celestialShimmer {
  0%, 100% { opacity: 0.6; }
  50% { opacity: 1; }
}
/* Navy + gold quote blocks */
.mes .mes_text q,
.mes .mes_text blockquote {
  border-left-color: rgba(227, 179, 95, 0.4) !important;
  background: rgba(0, 47, 134, 0.06);
}
/* Warm golden glow on send */
#send_but {
  text-shadow: 0 0 10px rgba(227, 179, 95, 0.5);
}
