custom_css:
/* ★ PG Guardian - Star Prism ★ */
/* Decorative star accents on messages */
.mes[is_user="false"] .mes_text::before {
  content: '★';
  position: absolute;
  top: -6px;
  left: -6px;
  font-size: 10px;
  color: rgba(243, 170, 30, 0.35);
  pointer-events: none;
}
.mes[is_user="false"] .mes_text::after {
  content: '✦';
  position: absolute;
  bottom: -4px;
  right: -4px;
  font-size: 8px;
  color: rgba(220, 120, 170, 0.25);
  pointer-events: none;
}
.mes .mes_text { position: relative; }
/* Gold accent line on bot messages */
.mes[is_user="false"] {
  border-left: 2px solid rgba(243, 170, 30, 0.2) !important;
}
/* Themed quote blocks */
.mes .mes_text q,
.mes .mes_text blockquote {
  border-left-color: rgba(220, 120, 170, 0.5) !important;
}
/* Subtle pink glow on send button area */
#send_but {
  text-shadow: 0 0 8px rgba(220, 120, 170, 0.4);
}
/* Star separator between messages */
.mes + .mes::before {
  content: '· ★ ·';
  display: block;
  text-align: center;
  font-size: 8px;
  color: rgba(243, 170, 30, 0.15);
  padding: 2px 0;
  letter-spacing: 4px;
}
