custom_css:
/* △ PG Phosphor - Neon △ */
/* Neon geometric accents with glow */
.mes[is_user="false"] .mes_text::before {
  content: '△';
  position: absolute;
  top: -6px;
  left: -6px;
  font-size: 10px;
  color: rgba(249, 144, 246, 0.4);
  text-shadow: 0 0 6px rgba(249, 144, 246, 0.3);
  pointer-events: none;
}
.mes[is_user="false"] .mes_text::after {
  content: '◎';
  position: absolute;
  bottom: -4px;
  right: -4px;
  font-size: 8px;
  color: rgba(115, 117, 202, 0.35);
  text-shadow: 0 0 4px rgba(115, 117, 202, 0.3);
  pointer-events: none;
}
.mes .mes_text { position: relative; }
/* Neon glow border on bot messages */
.mes[is_user="false"] {
  border-left: 2px solid rgba(249, 144, 246, 0.3) !important;
  box-shadow: inset 3px 0 15px rgba(249, 144, 246, 0.05);
}
/* Electric separator with glow */
.mes + .mes::before {
  content: '· ⚡ ·';
  display: block;
  text-align: center;
  font-size: 8px;
  color: rgba(249, 144, 246, 0.2);
  text-shadow: 0 0 4px rgba(249, 144, 246, 0.15);
  padding: 2px 0;
  letter-spacing: 4px;
}
/* Neon quote blocks */
.mes .mes_text q,
.mes .mes_text blockquote {
  border-left-color: rgba(249, 144, 246, 0.5) !important;
  background: rgba(115, 117, 202, 0.04);
  box-shadow: inset 2px 0 8px rgba(249, 144, 246, 0.03);
}
/* Hot pink neon glow on send */
#send_but {
  text-shadow: 0 0 10px rgba(249, 144, 246, 0.5);
}
/* Subtle CRT scanline effect on chat area */
#chat::after {
  content: '';
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: repeating-linear-gradient(
    to bottom,
    transparent 0px,
    transparent 2px,
    rgba(18, 1, 71, 0.015) 2px,
    rgba(18, 1, 71, 0.015) 4px
  );
  pointer-events: none;
  z-index: 9998;
}
