custom_css:
/* ❀ PG Rosewood - Floral ❀ */
/* Rose and dreamcatcher accents */
.mes[is_user="false"] .mes_text::before {
  content: '❀';
  position: absolute;
  top: -6px;
  left: -6px;
  font-size: 10px;
  color: rgba(228, 176, 188, 0.3);
  pointer-events: none;
}
.mes[is_user="false"] .mes_text::after {
  content: '❧';
  position: absolute;
  bottom: -4px;
  right: -4px;
  font-size: 9px;
  color: rgba(131, 182, 172, 0.25);
  pointer-events: none;
}
.mes .mes_text { position: relative; }
/* Soft sage border */
.mes[is_user="false"] {
  border-left: 2px solid rgba(131, 182, 172, 0.2) !important;
}
/* Rose vine separator */
.mes + .mes::before {
  content: '~ ♡ ~';
  display: block;
  text-align: center;
  font-size: 8px;
  color: rgba(228, 176, 188, 0.15);
  padding: 2px 0;
  letter-spacing: 4px;
}
/* Warm blush quotes */
.mes .mes_text q,
.mes .mes_text blockquote {
  border-left-color: rgba(228, 176, 188, 0.4) !important;
  background: rgba(228, 176, 188, 0.03);
}
/* Sage green glow */
#send_but {
  text-shadow: 0 0 8px rgba(131, 182, 172, 0.4);
}
